// page init
jQuery(function(){
	initTabs();
	jcf.customForms.replaceAll();
	initTouchNav();
	initSlideShow();
	initDropDown();
	initMultiColumns();
	initScalingNavigation();
	initChildClasses();
	initAddClasses();
	initPlanChoise();
});

function initPlanChoise() {
	jQuery('.zone-form').each(function() {
		var holder = jQuery(this);
		var blocks = holder.find('.planbox-container');
		var activeClass = 'active';

		blocks.click(function() {
			blocks.removeClass(activeClass);
			jQuery(this).addClass(activeClass);
		});
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		event: 'click',
		autoRotation: true,
		switchTime: 2000,
		animSpeed: 500
	});
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').contentTabs({
		addToParent: true,
		tabLinks: 'a'
	});
}

// animated navigation init
function initDropDown() {
	jQuery('ul#nav').animDropdown({
		items: 'li',
		drop: '>.drop',
		animSpeed: 200,
		effect: 'fade'
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this,
			menuDrop: '.drop'
		});
	});
}

// split content into columns
function initMultiColumns() {
	jQuery('.intro-servers .three-columns ul').columnize({
		buildOnce: true,
		columns: 2
	});
}

// resize navigation items to fill parent
function initScalingNavigation() {
	initAutoScalingNav({
		menuId: 'nav',
		spacing: 3,
		sideClasses: true
	});
}

// add classes to support css3 selectors in old browsers
function initChildClasses() {
	jQuery('.ovt-table tbody').children(':nth-child(even)').addClass('even');
	jQuery('.ovt-table tbody tr').children(':last-child').addClass('last-child');
	jQuery('.ovt-table tbody').children(':last-child').addClass('last-child');
	jQuery('.order-table tbody').children(':nth-child(even)').addClass('even');
}

// add class on click
function initAddClasses() {
	jQuery('.planbox-container').clickClass({
		classAdd: 'active'
	});
}

